   samm:preferredName "Notification Id"@en ;
   samm:description "A unique ID for the EW notification "@en ;
   samm:characteristic ext-uuid:UuidV4Trait ;
   samm:exampleValue "c2801472-5f87-41a7-9a25-b0939c4e0dff" .

