```turtle
@prefix samm: <urn:samm:org.eclipse.esmf.samm:meta-model:2.1.0#> .
@prefix samm-c: <urn:samm:org.eclipse.esmf.samm:characteristic:2.1.0#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix : <urn:samm:io.catenax.generic.digital_product_passport:2.1.0#> .

:data a samm:Property ;
   samm:preferredName "Data"@en ;
   samm:description "The content from the attribute which is a depended of the data type and typeUnit."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "23" .

:label a samm:Property ;
   samm:preferredName "Label"@en ;
   samm:description "The human readable name of the attribute."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "Maximum permitted battery power" .

:IdEntity a samm:Entity ;
   samm:preferredName "Id Entity"@en ;
   samm:description "Id Entity with identifier and list type."@en ;
   samm:properties ( [ samm:property :listTypeId; samm:payloadName "type" ] ) .

:CompanyIdentification a samm:Characteristic ;
   samm:preferredName "Company Identification"@en ;
   samm:description "Identification of the company or location of the company. In the CATENA-X network the BPN/L identification is needed."@en ;
   samm:dataType xsd:string .

:manufacturingDate a samm:Property ;
   samm:preferredName "Manufacturing Date"@en ;
   samm:description "The timestamp in the format (yyyy-mm-dd) of the manufacturing date as the final step in production process (e.g. final quality check, ready-for-shipment event)."@en ;
   samm:characteristic :DateTrait ;
   samm:exampleValue "2000-01-31" .

:PhysicalStateEnumeration a samm-c:Enumeration ;
   samm:preferredName "Physical State Enumeration"@en ;
   samm:description "Enumeration with the values solid, liquid, gas and plasma."@en ;
   samm:dataType xsd:string ;
   samm-c:values (
      "solid"
      "liquid"
      "gas"
      "plasma"
   ) .

:SustainabilityCharacteristic a samm:Characteristic ;
   samm:preferredName "Sustainability Characteristic"@en ;
   samm:description "Characteristic which describes relevant information for the sustainability of the product."@en ;
   samm:dataType :SustainabilityEntity .

:CriticalList a samm-c:List ;
   samm:preferredName "Critical List"@en ;
   samm:description "The list of critical raw materials in the product (CRM) which is specified by EU."@en ;
   samm:dataType xsd:string .

:SustainabilityEntity a samm:Entity ;
   samm:preferredName "Sustainability Entity"@en ;
   samm:description "Entity with the properties state, al list of different materials, a list of critical materials and the product carbon footprint."@en ;
   samm:properties ( :critical :state :material ) .

:material a samm:Property ;
   samm:preferredName "Material"@en ;
   samm:description "Information on different materials in the product."@en ;
   samm:characteristic :EitherMaterialCharacteristic .

:NameEntity a samm:Entity ;
   samm:preferredName "Name Entity"@en ;
   samm:description "Entity with the name and used list type."@en ;
   samm:properties ( :name ) .

:listTypeId a samm:Property ;
   samm:preferredName "List Type Id"@en ;
   samm:description "The type of list used for the identification of the substances. Selected can be for example CAS or EC."@en ;
   samm:characteristic :ListTypeEnumerationId ;
   samm:exampleValue "CAS" .

:ClassEntity a samm:Entity ;
   samm:preferredName "Class Entity"@en ;
   samm:description "Entity to describe the associated product category according to ECLASS. ECLASS is a monohierachical classification system where each class has a preferred name and a coded name that represents the classification structure."@en ;
   samm:properties ( :definition ) .

:characteristics a samm:Property ;
   samm:preferredName "Characteristics"@en ;
   samm:description "Defines specific characteristics of a product."@en ;
   samm:characteristic :Characteristics .

:ConcentrationEnumeration a samm-c:Enumeration ;
   samm:preferredName "Concentration Enumeration"@en ;
   samm:description "Enumeration of possible units of concentration with percent, volume percent, parts per thousand, parts per million, parts per billion and parts per trillion."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:dataType xsd:string ;
   samm-c:values (
      "unit:partPerMillion"
      "unit:percent"
      "unit:percentVolume"
      "unit:partPerThousand"
      "unit:partPerTrillionUs"
      "unit:partPerBillionUs"
   ) .

:ManufacturerEntity a samm:Entity ;
   samm:preferredName "Manufacturer Entity"@en ;
   samm:description "Manufacturing Entity with the identification of the main manufacturer and the facility location as well as the manufacturing date."@en ;
   samm:properties (
      [ samm:property :MANUFACTURERIDENTIFICATION ]
      [ samm:property :manufacturingDate; samm:optional true ]
   ) .

:SubstanceEntity a samm:Entity ;
   samm:preferredName "Substance Entity"@en ;
   samm:description "Information regarding substances of concern in the product. Attributes are among others substance names, ids, concentration, location. This is mentioned in the ESPR proposal from March 30th, 2022 and changed by the amendments of the European Parliament from July 12th, 2023 Article 7:\n(5) (a) the International Union of Pure and Applied Chemistry (IUPAC) name of the substances of concern present in the product, including the chemical identification number, that is the European Inventory of Existing Commercial Chemical Substances (EINECS) or European List of Notified Chemical Substances (ELINCS) number or Chemical Abstract Service (CAS) number.\n(5) (b) the location of the substances of concern within the product.\n(5) (c) the concentration, maximum concentration or concentration range of the substances of concern, at the level of the product [...].\n(5) (c) provide exemptions for substances of concern or information elements from the information requirements referred to in the first subparagraph."@en ;
   samm:properties (
      [ samm:property :substanceIdentification; samm:payloadName "id" ]
      :CONCENTRATION
      [ samm:property :SUBSTANCEUNIT ]
      :location
   ) .

:critical a samm:Property ;
   samm:preferredName "Critical"@en ;
   samm:description "A list of critical raw material names which are included at the level of the product. This attribute is complemented to the ESPR proposal by the amendments of the European Parliament from July 12th, 2023 Annex I:\n(hb) use or content of critical raw materials.\nIn Annex II of the connected proposal Act of Critical Raw Materials, a list of critical raw materials can be found."@en ;
   samm:see <https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=COM:2023:0160:FIN> ;
   samm:characteristic :EitherCritical .

:HandlingEntity a samm:Entity ;
   samm:preferredName "Handling Entity"@en ;
   samm:description "Entity to describe different aspects in relation with the handling of the product. Properties are the maintenance history spare parts, substances of concern and reusable packaging."@en ;
   samm:properties ( :substanceOfConcern ) .

:OperationCharacteristic a samm:Characteristic ;
   samm:preferredName "Operation Characteristic"@en ;
   samm:description "Operational information of the product."@en ;
   samm:dataType :OperationEntity .

:DateConstraint a samm-c:RegularExpressionConstraint ;
   samm:preferredName "Date Constraint"@en ;
   samm:description "Constraint fo a timestamp in the format (yyyy-mm-dd)."@en ;
   samm:value "^\\d{4}-\\d{2}-\\d{2}$" .

:OperationEntity a samm:Entity ;
   samm:preferredName "Operation Entity"@en ;
   samm:description "Operational information of the product such as the owner, manufacturer and importer."@en ;
   samm:properties ( :manufacturer ) .

:RangeEntity a samm:Entity ;
   samm:preferredName "Range Entity"@en ;
   samm:description "Entity for the concentration range with two values, the highest and the lowest of the concentration range."@en ;
   samm:properties ( [ samm:property :maxConcentration; samm:payloadName "max" ] ) .

:maxConcentration a samm:Property ;
   samm:preferredName "Maximum Concentration"@en ;
   samm:description "The maximum concentration of the substance of concern at the level of the product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Article 7:\n(5) (c) the concentration, maximum concentration or concentration range of the substances of concern, at the level of the product [...]."@en ;
   samm:characteristic :ConcentrationValue ;
   samm:exampleValue "2.6"^^xsd:float .

:additionalData a samm:Property ;
   samm:preferredName "Additional Data"@en ;
   samm:description "Data in form of open fields which need to be transferred in addition. The regulation is still under development and may change in the future. To accommodate this, additional data allows the option to include additional data required by future changes to the regulation. In addition, the DPP can be easily updated and adapted to the new requirements."@en ;
   samm:characteristic :AdditionalDataList .

:NotApplicableEnumeration a samm-c:Enumeration ;
   samm:preferredName "Not Applicable Enumeration"@en ;
   samm:description "Enumeration with the value \"not applicable\"."@en ;
   samm:dataType xsd:string ;
   samm-c:values ( "not applicable" ) .

:TypologyCharacteristic a samm:Characteristic ;
   samm:preferredName "Typology Characteristic"@en ;
   samm:description "Characteristic to further describe product."@en ;
   samm:dataType :TypologyEntity .

:StateEnumeration a samm-c:Enumeration ;
   samm:preferredName "State Enumeration"@en ;
   samm:description "Enumeration with the values first life and second life."@en ;
   samm:dataType xsd:string ;
   samm-c:values ( "first life" "second life" ) .

:sustainability a samm:Property ;
   samm:preferredName "sustainability"@en ;
   samm:description "Properties which are relevant for the sustainability of the product."@en ;
   samm:characteristic :SustainabilityCharacteristic .

:Characteristics a samm:Characteristic ;
   samm:preferredName "Characteristics"@en ;
   samm:description "Defines a set of specific characteristics of a product."@en ;
   samm:dataType :CharacteristicsEntity .

:CONCENTRATION a samm:Property ;
   samm:preferredName "Concentration"@en ;
   samm:description "Concentration of the substance of concern at the level of the product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Article 7:\n(5) (c) the concentration, maximum concentration or concentration range of the substances of concern, at the level of the product [...]."@en ;
   samm:characteristic :EitherConcentration .

:CommercialCharacteristic a samm:Characteristic ;
   samm:preferredName "Commercial Characteristic"@en ;
   samm:description "Commercial information of the product."@en ;
   samm:dataType :CommercialEntity .

:SUBSTANCEUNIT a samm:Property ;
   samm:preferredName "Substance Unit"@en ;
   samm:description "The unit of concentration chosen from an enumeration: mass percent, volume percent, parts per thousand, parts per million, parts per billion and  parts per trillion."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:characteristic :ConcentrationEnumeration ;
   samm:exampleValue "unit:percent" .

:ListTypeEnumerationId a samm-c:Enumeration ;
   samm:preferredName "List Type Enumeration Id"@en ;
   samm:description "Enumeration of different systems and organizations related to the identification and classification of chemical substances in the field of chemistry. The enumeration values are EC or CAS."@en ;
   samm:see <https://www.cas.org/cas-data/cas-registry> ;
   samm:dataType xsd:string ;
   samm-c:values ( "CAS" "EC" ) .

:SubstanceCharacteristic a samm-c:Either ;
   samm:preferredName "Substance Characteristic"@en ;
   samm:description "Either a list of substances (left) or the string value \"not applicable\" (right)."@en ;
   samm-c:left :SubstanceList ;
   samm-c:right :NotApplicableEnumeration .

:LifespanEntity a samm:Entity ;
   samm:preferredName "Lifespan Entity"@en ;
   samm:description "Entity for the lifespan of a product with type, unit and value."@en ;
   samm:properties (
      [ samm:property :lifeValue; samm:payloadName "value" ]
      [ samm:property :lifeUnit; samm:payloadName "unit" ]
   ) .

:RENEWABLE a samm:Property ;
   samm:preferredName "Renewable"@en ;
   samm:description "A flag if the material is from a renewable resource that can be replenished. Renewable resources are those that can be reproduced by physical, chemical, or mechanical processes. These are the kind of resources that can be regenerated throughout time. Forest wood, for example, can be grown through reforestation. This attribute is complemented to the ESPR proposal by the amendments of the European Parliament from July 12th, 2023 Annex I:\n(ha) use or content of sustainably sourced renewable materials."@en ;
   samm:characteristic samm-c:Boolean ;
   samm:exampleValue true .

:lifeValue a samm:Property ;
   samm:preferredName "Life Value"@en ;
   samm:description "The value as an integer for the respective lifespan."@en ;
   samm:characteristic :LifeValueCharacteristic ;
   samm:exampleValue 36 .

:longName a samm:Property ;
   samm:preferredName "Long Name"@en ;
   samm:description "The full name of the product as a general short product description. This provides the users of the product passport with relevant information about the product. It can also be a marketing slogan or short description with one to a few sentences."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "Product Description long text" .

:MaterialEntity a samm:Entity ;
   samm:preferredName "Material Entity"@en ;
   samm:description "Information regarding recycled and/or renewable materials in the product. Attributes are among others material names, ids and percentage. These attributes are mentioned in the ESPR proposal from March 30th, 2022 Article 1:\n(1) (f) recycled content in products.\nAnd complemented by the amendments of the European Parliament from July 12th, 2023 Annex I:\n(ha) use or content of sustainably sourced renewable materials."@en ;
   samm:properties (
      :recycled
      [ samm:property :SUBSTANCEUNIT ]
      [ samm:property :materialName; samm:payloadName "name" ]
      :RENEWABLE
   ) .

:manufacturer a samm:Property ;
   samm:preferredName "Manufacturer"@en ;
   samm:description "Manufacturing information of the product. In the CATENA-X use case, the BPNL and BPNS can be stated. These attributes are mentioned in the ESPR proposal from March 30th, 2022 Annex III:\n(g) information related to the manufacturer, such as its unique operator identifier and the information referred to in Article 21(7);\n(i) unique facility identifiers."@en ;
   samm:characteristic :ManufacturerCharacteristic .

:dataType a samm:Property ;
   samm:preferredName "Data Type"@en ;
   samm:description "Data type that describe the content of the attributes children and data. In case \"object\" is selected in the enumeration, the children field will be used in the AdditionalDataEntity instead of the \"data\" property. If it is an other type, the content will be specified in \"data\" as a string."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:characteristic :DataTypeEnumeration ;
   samm:exampleValue "xsd:integer" .

:CommercialEntity a samm:Entity ;
   samm:preferredName "Commercial Entity"@en ;
   samm:description "Commercial information such as placed on market and the warranty."@en ;
   samm:properties ( [ samm:property :PLACEDONMARKET; samm:optional true ] ) .

:TypeCharacteristic a samm:Characteristic ;
   samm:preferredName "Type Characteristic"@en ;
   samm:description "Characteristic for the data type."@en ;
   samm:dataType :TypeEntity .

:ClassCharacteristic a samm:Characteristic ;
   samm:preferredName "Class Characteristic"@en ;
   samm:description "Characteristic to describe the ECLASS typology of a product."@en ;
   samm:dataType :ClassEntity .

:DateTrait a samm-c:Trait ;
   samm:preferredName "Date Trait"@en ;
   samm:description "Trait for valid traits.."@en ;
   samm-c:baseCharacteristic samm-c:Text ;
   samm-c:constraint :DateConstraint .

:EitherConcentration a samm-c:Either ;
   samm:preferredName "Either Concentration"@en ;
   samm:description "Characteristic with two possible values. Either the concentration range, which can be either the range or only the maximum concentration (left) or a single concentration (right)."@en ;
   samm-c:left :RangeCharacteristic ;
   samm-c:right :ConcentrationCharacteristic .

:AdditionalDataEntity a samm:Entity ;
   samm:preferredName "Additional Data Entity"@en ;
   samm:description "Entity with additional data. Properties are label, description, type, data and children. Either the fields data or children filled. Children constructs a hierarchy with the same attributes. Data is filled as the end of an path/leaf."@en ;
   samm:properties ( :type [ samm:property :data; samm:optional true ] :label ) .

:lifeUnit a samm:Property ;
   samm:preferredName "Life Unit"@en ;
   samm:description "The unit of the respective lifespan expressed through the possible units day, month, cycle, year and runningOrOperatingHour."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:characteristic :LifeEnumeration ;
   samm:exampleValue "unit:month" .

:MaterialList a samm-c:List ;
   samm:preferredName "Material List"@en ;
   samm:description "A list of different materials in the product."@en ;
   samm:dataType :MaterialEntity .

:state a samm:Property ;
   samm:preferredName "State"@en ;
   samm:description "The status of the product (first life or second life) to indicated, whether it is a used product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Annex I:\n(j) incorporation of used components."@en ;
   samm:characteristic :StateEnumeration ;
   samm:exampleValue "first life" .

:DataTypeEnumeration a samm-c:Enumeration ;
   samm:preferredName "Data Type Enumeration"@en ;
   samm:description "Data type that describe the content of the attributes children and data. In case \"object\" is selected in the enumeration, the children field will be used in the AdditionalDataEntity instead of the \"data\" property. If it is an other type, the content will be specified in \"data\" as a string."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:see <https://www.w3.org/2001/XMLSchema> ;
