   samm:see <https://eur-lex.europa.eu/legal-content/EN/TXT/PDF/?uri=CONSIL:ST_5147_2024_INIT> ;
   samm:properties (
      :identification
      :COMMERCIAL
      :characteristics
      :operation
   ) ;
   samm:operations ( ) ;
