@prefix samm: <urn:samm:org.eclipse.esmf.samm:meta-model:2.1.0#> .
@prefix samm-c: <urn:samm:org.eclipse.esmf.samm:characteristic:2.1.0#> .
@prefix unit: <urn:samm:org.eclipse.esmf.samm:unit:2.1.0#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix : <urn:samm:io.catenax.generic.digital_product_passport:3.1.0#> .
@prefix ext-address: <urn:samm:io.catenax.shared.address_characteristic:3.0.0#> .
@prefix ext-contact: <urn:samm:io.catenax.shared.contact_information:3.0.0#> .
@prefix ext-number: <urn:samm:io.catenax.shared.business_partner_number:1.0.0#> .
@prefix ext-quantity: <urn:samm:io.catenax.shared.quantity:1.0.0#> .
@prefix ext-uuid: <urn:samm:io.catenax.shared.uuid:1.0.0#> .

:NameCharacteristic a samm:Characteristic ;
   samm:preferredName "Name Characteristic"@en ;
   samm:description "Name Characteristic for the definition of the name."@en ;
   samm:dataType :NameEntity .

:NotApplicableEnumeration a samm-c:Enumeration ;
   samm:preferredName "Not Applicable Enumeration"@en ;
   samm:description "Enumeration with the value \"not applicable\"."@en ;
   samm:dataType xsd:string ;
   samm-c:values ( "not applicable" ) .

:footprintRulebook a samm:Property ;
   samm:preferredName "Footprint Rulebook"@en ;
   samm:description "The applied rulebook for the environmental footprint of the product."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "https://www.alink.pdf/" .

:PHYSICALDIMENSION a samm:Property ;
   samm:preferredName "Physical Dimension"@en ;
   samm:description "Physical dimensions are properties  associated with physical quantities for purposes of classification or differentiation. Mentioned are a part of these requirements in the ESPR proposal from March 30th, 2022 Article 7:\n(2) (b) (i) information on the performance of the product in relation to the product parameters referred to in Annex I;\nAnnex I (i) weight and volume of the product and its packaging, and the product-to-packaging ratio."@en ;
   samm:characteristic :PhysicalDimensionCharacteristic .

:substanceUnit a samm:Property ;
   samm:preferredName "Substance Unit"@en ;
   samm:description "The unit of concentration chosen from an enumeration: mass percent, volume percent, parts per thousand, parts per million, parts per billion and  parts per trillion."@en ;
   samm:see <https://eclipse-esmf.github.io/samm-specification/2.1.0/index.html> ;
   samm:characteristic :ConcentrationEnumeration ;
   samm:exampleValue "unit:percent" .

:MetadataEntity a samm:Entity ;
   samm:preferredName "Metadata Entity"@en ;
   samm:description "Passport Entity to describe version, status, end and issue date."@en ;
   samm:properties (
      :expirationDate
      :issueDate
      :predecessor
      :version
      [ samm:property :status; samm:optional true ]
   ) .

:NameEntity a samm:Entity ;
   samm:preferredName "Name Entity"@en ;
   samm:description "Entity with the name and used list type."@en ;
   samm:properties ( [ samm:property :listTypeName; samm:payloadName "type" ] ) .

:FootprintList a samm-c:List ;
   samm:preferredName "Footprint List"@en ;
   samm:description "Footprint List for the environmental footprint."@en ;
   samm:dataType :FootprintEntity .

:HeaderConstraint a samm-c:LengthConstraint ;
   samm:preferredName "Header Constraint"@en ;
   samm:description "Constraint for a maximum of 100 characters."@en ;
   samm-c:maxValue "100"^^xsd:nonNegativeInteger .

:length a samm:Property ;
   samm:preferredName "length"@en ;
   samm:description "The length of the item measured in a specific linear unit which can be declared in the corresponding unit attribute."@en ;
   samm:characteristic ext-quantity:LinearCharacteristic .

:performanceClass a samm:Property ;
   samm:preferredName "Performance Class"@en ;
   samm:description "The performance classification of the footprint."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "A" .

:ClassEntity a samm:Entity ;
   samm:preferredName "Class Entity"@en ;
   samm:description "Entity to describe the associated product category according to ECLASS. ECLASS is a monohierachical classification system where each class has a preferred name and a coded name that represents the classification structure."@en ;
   samm:properties ( :definition ) .

:CharacteristicsEntity a samm:Entity ;
   samm:preferredName "Characteristics Entity"@en ;
   samm:description "Entity which defines specific characteristics of a product such as different life spans and physical dimensions."@en ;
   samm:properties ( :PHYSICALDIMENSION :lifespan ) .

:HAZARDCLASS a samm:Property ;
   samm:description "The specification of the hazard class."@en ;
   samm:see <https://echa.europa.eu/et/support/mixture-classification/hazard-class-table> ;
   samm:see <https://echa.europa.eu/regulations/clp/classification> ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "Acute toxicity" .

:critical a samm:Property ;
   samm:preferredName "Critical"@en ;
   samm:description "A list of critical raw material names and their share which are included at the level of the product. This attribute is complemented to the ESPR proposal by the amendments of the European Parliament from July 12th, 2023 Annex I:\n(hb) use or content of critical raw materials.\nIn Annex II of the connected proposal Act of Critical Raw Materials, a list of critical raw materials can be found."@en ;
   samm:see <https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=COM:2023:0160:FIN> ;
   samm:characteristic :EitherCritical .

:EitherCritical a samm-c:Either ;
   samm:preferredName "Either Critical"@en ;
   samm:description "Either a list of critical materials (left) or the string value \"not applicable\" (right)."@en ;
   samm-c:left :CriticalList ;
   samm-c:right :NotApplicableEnumeration .

:lifeType a samm:Property ;
   samm:preferredName "Life Type"@en ;
   samm:description "The type of lifespan represented with the values guaranteed lifetime, technical lifetime and mean time between failures. This attribute is mentioned in the ESPR proposal from March 30th, 2022 ANNEX I:\n(a) durability and reliability of the product or its components as expressed through the product?s guaranteed lifetime, technical lifetime [or] mean time between failures [...]."@en ;
   samm:characteristic :LifeTypeEnumeration ;
   samm:exampleValue "guaranteed lifetime" .

:TypologyCharacteristic a samm:Characteristic ;
   samm:preferredName "Typology Characteristic"@en ;
   samm:description "Characteristic to further describe product."@en ;
   samm:dataType :TypologyEntity .

:materialValue a samm:Property ;
   samm:preferredName "Material Value"@en ;
   samm:description "Concentration of the material in correlation to the material sum."@en ;
   samm:characteristic :ConcentrationValue ;
   samm:exampleValue "5.0"^^xsd:float .

:HeaderTrait a samm-c:Trait ;
   samm-c:baseCharacteristic samm-c:Text ;
   samm-c:constraint :HeaderConstraint .

:SubstanceCharacteristic a samm-c:Either ;
   samm:preferredName "Substance Characteristic"@en ;
   samm:description "Either a list of substances (left) or the string value \"not applicable\" (right)."@en ;
   samm-c:left :SubstanceList ;
   samm-c:right :NotApplicableEnumeration .

:productEnvironmentalFootprint a samm:Property ;
   samm:preferredName "Product Environmental Footprint"@en ;
   samm:description "The carbon and environmental footprint of the product. These attributes are mentioned in the ESPR proposal from March 30th, 2022 Annex I:\n(l) the environmental footprint of the product, expressed as a quantification, in accordance with the applicable delegated act, of a product?s life cycle environmental impacts, whether in relation to one or more environmental impact categories or an aggregated set of impact categories;\n(m) the carbon footprint of the product.\nBoth are defined by Article 2:\n(23) ?environmental footprint? means a quantification of a product?s environmental impacts, whether in relation to a single environmental impact category or an aggregated set of impact categories based on the Product Environmental Footprint method;\n(25) ?carbon footprint? means the sum of greenhouse gas (GHG) emissions and GHG removals in a product system, expressed as CO2 equivalents and based on a life cycle assessment using the single impact category of climate change."@en ;
   samm:characteristic :ProductEnvironmentalFootprintCharacteristic .

:LifeTypeEnumeration a samm-c:Enumeration ;
   samm:preferredName "Life Type Enumeration"@en ;
   samm:description "Enumeration with the values guaranteed lifetime, technical lifetime and mean time between failures."@en ;
   samm:dataType xsd:string ;
   samm-c:values ( "guaranteed lifetime" "technical lifetime" "mean time between failures" ) .

:DocumentEntity a samm:Entity ;
   samm:preferredName "Document Entity"@en ;
   samm:description "Document entity with header, content, category and type."@en ;
   samm:properties ( :content [ samm:property :contentType; samm:payloadName "type" ] :HEADER ) .

:StatusEnumeration a samm-c:Enumeration ;
   samm:preferredName "Status Enumeration"@en ;
   samm:description "The current status of the product passport declared through either: draft, approved, invalid or expired."@en ;
   samm:dataType xsd:string ;
   samm-c:values (
      "draft"
      "approved"
      "invalid"
      "expired"
   ) .

:predecessor a samm:Property ;
   samm:preferredName "Predecessor"@en ;
   samm:description "Identification of the preceding product passport. This could either be an identification of the registered twin, a redirection to a platform where it is available, or if there is no preceding passport, input null."@en ;
   samm:characteristic :IdentificationEither ;
   samm:exampleValue "null" .

:LifespanList a samm-c:List ;
   samm:preferredName "Lifespan List"@en ;
   samm:description "List of different life spans of a product."@en ;
   samm:dataType :LifespanEntity .

:EoriConstraint a samm-c:RegularExpressionConstraint ;
   samm:preferredName "EORI Constraint"@en ;
   samm:description "Constraint with a pattern which ensures that the EORI number starts with two alphanumeric characters, followed by two alphabetic characters, and ends with 2 to 15 alphanumeric characters."@en ;
   samm:value "^[A-Z]{2}[A-Z0-9]{1,18}$" .

:substanceOfConcern a samm:Property ;
   samm:preferredName "Concerning Substance"@en ;
   samm:description "Information regarding substances of concern in the product. Attributes are among others substance names, ids, concentration, location and hazard class. This is mentioned in the ESPR proposal from March 30th, 2022 and changed by the amendments of the European Parliament from July 12th, 2023 Article 7:\n(5) (a) the International Union of Pure and Applied Chemistry (IUPAC) name of the substances of concern present in the product, including the chemical identification number, that is the European Inventory of Existing Commercial Chemical Substances (EINECS) or European List of Notified Chemical Substances (ELINCS) number or Chemical Abstract Service (CAS) number.\n(5) (b) the location of the substances of concern within the product.\n(5) (c) the concentration, maximum concentration or concentration range of the substances of concern, at the level of the product [...].\n(5) (c) provide exemptions for substances of concern or information elements from the information requirements referred to in the first subparagraph."@en ;
   samm:characteristic :SubstanceCharacteristic .

:LIFEVALUE a samm:Property ;
   samm:preferredName "Life Value"@en ;
   samm:description "The value as an integer for the respective lifespan."@en ;
   samm:characteristic :LifeValueCharacteristic ;
   samm:exampleValue 36 .

:eori a samm:Property ;
   samm:preferredName "EORI"@en ;
   samm:description "An economic operator established in the customs territory of the Union needs, for customs purposes, an EORI number. EORI stands for economic operators registration and identification number. In this case, the importer's EORI must be provided. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Annex III:\n(j) information related to the importer, including the information referred to in Article 23(3) and its EORI number."@en ;
   samm:see <https://taxation-customs.ec.europa.eu/customs-4/customs-procedures-import-and-export-0/customs-procedures/economic-operators-registration-and-identification-number-eori_en> ;
   samm:characteristic :EoriTrait ;
   samm:exampleValue "GB123456789000" .

:MaterialList a samm-c:List ;
   samm:preferredName "Material List"@en ;
   samm:description "A list of different materials in the product."@en ;
   samm:dataType :MaterialEntity .

:IMPORTER a samm:Property ;
   samm:preferredName "Importer"@en ;
   samm:description "Information regarding the importer of the product, if different from the owner of the passport. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Annex III:\n(j) information related to the importer, including the information referred to in Article 23(3) and its EORI number;\nArticle 23 states:\n(3) Importers shall indicate on the product their name, registered trade name or registered trade mark and the postal address and, where available, electronic means of communication, where they can be contacted or, where this is not possible, on the packaging, in a document accompanying the product or, where available, in a product passport. The contact details shall be clear, understandable and legible."@en ;
   samm:characteristic :EitherImporter .

:Share a samm-c:Measurement ;
   samm:preferredName "share"@en ;
   samm:description "The share represented in a float number in the unit percent."@en ;
   samm:dataType xsd:float ;
   samm-c:unit unit:percent .

:ConcentrationValue a samm-c:Quantifiable ;
   samm:preferredName "Concentration Value"@en ;
   samm:description "Float number representing a concentration."@en ;
   samm:dataType xsd:float .

:ConcentrationCharacteristic a samm:Characteristic ;
   samm:preferredName "Concentration Characteristic"@en ;
   samm:description "Single concentration characteristic for the actual concentration."@en ;
   samm:dataType :SingleEntity .

:footprintValue a samm:Property ;
   samm:preferredName "Footprint Value"@en ;
   samm:description "The value of the environmental footprint or carbon footprint of the product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Annex I:\n(l) the environmental footprint of the product, expressed as a quantification, in accordance with the applicable delegated act, of a product?s life cycle environmental impacts, whether in relation to one or more environmental impact categories or an aggregated set of impact categories;\n(m) the carbon footprint of the product.\n"@en ;
   samm:characteristic :FootprintValueCharacteristic ;
   samm:exampleValue "12.678"^^xsd:float .

:OperationEntity a samm:Entity ;
   samm:preferredName "Operation Entity"@en ;
   samm:description "Operational information of the product such as the owner, manufacturer and importer."@en ;
   samm:properties ( :IMPORTER ) .

:diameter a samm:Property ;
   samm:preferredName "diameter"@en ;
   samm:description "The diameter of the item, if applicable, measured in a specific linear unit which can be declared in the corresponding unit attribute."@en ;
   samm:characteristic ext-quantity:LinearCharacteristic .

:class a samm:Property ;
   samm:preferredName "Class"@en ;
   samm:description "The associated product category according to ECLASS. The ECLASS is the worldwide reference data standard for classifying and clearly describing products and services."@en ;
   samm:see <https://eclass.eu/en/eclass-standard/search-content/show?tx_eclasssearch_ecsearch%5Bdischarge%5D=0&tx_eclasssearch_ecsearch%5Bid%5D=44000000&tx_eclasssearch_ecsearch%5Blanguage%5D=1&tx_eclasssearch_ecsearch%5Bversion%5D=13.0&cHash=10abd2696c4c5c526da4e3367e78c592> ;
   samm:characteristic :ClassCharacteristic .

:content a samm:Property ;
   samm:preferredName "Content"@en ;
   samm:description "The content of the document e.g a link."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "www.alink.pdf" .

:footprintUnit a samm:Property ;
   samm:preferredName "Footprint Unit"@en ;
   samm:description "The unit of measurement of the environmental impact category. For each impact category a specific unit is used. For climate change/carbon footprint \"kg CO2 eq\" will be used. If an aggregation is used, utilize the normalization and weighting methods used in the referenced rulebook."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "kg CO2 eq" .

:SourceList a samm-c:List ;
   samm:preferredName "Source List"@en ;
   samm:description "A list of documents."@en ;
   samm:dataType :DocumentEntity .

:TypologyEntity a samm:Entity ;
   samm:preferredName "Typology Entity"@en ;
   samm:description "Entity describing attributes such class and different names of the product."@en ;
   samm:properties ( [ samm:property :class; samm:optional true ] :SHORTNAME ) .

:footprintLifecycle a samm:Property ;
   samm:preferredName "Footprint Lifecycle"@en ;
   samm:description "The lifecycle stage, to which the environmental footprint corresponds. These could be for example \"raw material acquisition and pre-processing\", \"main product production\", \"distribution\" or \"end of life and recycling\"."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "main product production" .

:HandlingEntity a samm:Entity ;
   samm:preferredName "Handling Entity"@en ;
   samm:description "Entity to describe different aspects in relation with the handling of the product. Properties are the maintenance history spare parts, substances of concern and reusable packaging."@en ;
   samm:properties ( :substanceOfConcern ) .

:contentType a samm:Property ;
   samm:preferredName "Content Type"@en ;
   samm:description "The type of content which can be expected in the \"content\" property. Examples are a link, restricted link, pdf, excel, etc."@en ;
   samm:characteristic :SourceEnumeration ;
   samm:exampleValue "URL" .

:DateTrait a samm-c:Trait ;
   samm:preferredName "Date Trait"@en ;
   samm:description "Trait for valid traits.."@en ;
   samm-c:baseCharacteristic samm-c:Text ;
   samm-c:constraint :DateConstraint .

:substanceIdentification a samm:Property ;
   samm:preferredName "Substance Identification"@en ;
   samm:description "The substance identification, in accordance with the specification in the attribute for the list type. This attribute is mentioned in the ESPR proposal from March 30th, 2022 and changed by the amendments of the European Parliament from July 12th, 2023 Article 7:\n(5) (a) the International Union of Pure and Applied Chemistry (IUPAC) name of the substances of concern present in the product, including the chemical identification number, that is the European Inventory of Existing Commercial Chemical Substances (EINECS) or European List of Notified Chemical Substances (ELINCS) number or Chemical Abstract Service (CAS) number."@en ;
   samm:characteristic :MaterialIdList .

:EitherConcentration a samm-c:Either ;
   samm:preferredName "Either Concentration"@en ;
   samm:description "Characteristic with two possible values. Either the concentration range, which can be either the range or only the maximum concentration (left) or a single concentration (right)."@en ;
   samm-c:left :RangeCharacteristic ;
   samm-c:right :ConcentrationCharacteristic .

:RangeEntity a samm:Entity ;
   samm:preferredName "Range Entity"@en ;
   samm:description "Entity for the concentration range with two values, the highest and the lowest of the concentration range."@en ;
   samm:properties ( [ samm:property :minConcentration; samm:optional true; samm:payloadName "min" ] ) .

:HEADER a samm:Property ;
   samm:preferredName "Header"@en ;
   samm:description "The header as a short description of the document with a maximum of 100 characters."@en ;
   samm:characteristic :HeaderTrait ;
   samm:exampleValue "Sustainability Document Material XY" .

:renewable a samm:Property ;
   samm:preferredName "Renewable"@en ;
   samm:description "The share of the material, which is from a renewable resource that can be replenished. Renewable resources are those that can be reproduced by physical, chemical, or mechanical processes. These are the kind of resources that can be regenerated throughout time. Forest wood, for example, can be grown through reforestation. This attribute is complemented to the ESPR proposal by the amendments of the European Parliament from July 12th, 2023 Annex I:\n(ha) use or content of sustainably sourced renewable materials."@en ;
   samm:characteristic :Share ;
   samm:exampleValue "23.5"^^xsd:float .

:ImporterCharacteristic a samm:Characteristic ;
   samm:preferredName "Importer Characteristic"@en ;
   samm:description "Characteristic with information regarding the importer."@en ;
   samm:dataType :ImporterEntity .

:SUSTAINABILITY a samm:Property ;
   samm:preferredName "sustainability"@en ;
   samm:description "Properties which are relevant for the sustainability of the product."@en ;
   samm:characteristic :SustainabilityCharacteristic .

:handling a samm:Property ;
   samm:preferredName "handling"@en ;
   samm:description "Properties connected with the handling of the product."@en ;
   samm:characteristic :HandlingCharacteristic .

:ProductEnvironmentalFootprintEntity a samm:Entity ;
   samm:preferredName "Product Environmental Footprint Entity"@en ;
   samm:description "The Product Environmental Footprint Entity with sustainability information such as the different environmental footprint types, the carbon footprint explicitly, applicable rules and lifecycle stages."@en ;
   samm:properties ( [ samm:property :CARBONFOOTPRINT ] ) .

:listTypeName a samm:Property ;
   samm:preferredName "List Type Name"@en ;
   samm:description "The type of list used for the identification of the substances. Selected can be for example IUPAC, EC or CAS."@en ;
   samm:characteristic :ListTypeEnumerationName ;
   samm:exampleValue "CAS" .

:characteristics a samm:Property ;
   samm:preferredName "Characteristics"@en ;
   samm:description "Defines specific characteristics of a product."@en ;
   samm:characteristic :Characteristics .

:SingleEntity a samm:Entity ;
   samm:preferredName "Single Entity"@en ;
   samm:description "Entity for a single concentration for the maximum or actual concentration."@en ;
   samm:properties ( [ samm:property :concentrationSingle; samm:payloadName "value" ] ) .

:IdentifierCharacteristic a samm:Characteristic ;
   samm:preferredName "Identifier Characteristic"@en ;
   samm:description "Characteristic for Identifiers."@en ;
   samm:dataType xsd:string .

:SubstanceEntity a samm:Entity ;
   samm:preferredName "Substance Entity"@en ;
   samm:description "Information regarding substances of concern in the product. Attributes are among others substance names, ids, concentration, location. This is mentioned in the ESPR proposal from March 30th, 2022 and changed by the amendments of the European Parliament from July 12th, 2023 Article 7:\n(5) (a) the International Union of Pure and Applied Chemistry (IUPAC) name of the substances of concern present in the product, including the chemical identification number, that is the European Inventory of Existing Commercial Chemical Substances (EINECS) or European List of Notified Chemical Substances (ELINCS) number or Chemical Abstract Service (CAS) number.\n(5) (b) the location of the substances of concern within the product.\n(5) (c) the concentration, maximum concentration or concentration range of the substances of concern, at the level of the product [...].\n(5) (c) provide exemptions for substances of concern or information elements from the information requirements referred to in the first subparagraph."@en ;
   samm:properties (
      [ samm:property :HAZARDCLASS; samm:optional true ]
      :concentration
      :exemption
      [ samm:property :substanceName; samm:payloadName "name" ]
      [ samm:property :substanceIdentification; samm:payloadName "id" ]
   ) .

:minConcentration a samm:Property ;
   samm:preferredName "Minimum Concentration"@en ;
   samm:description "The minimum concentration of the substance of concern at the level of the product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 Article 7:\n(5) (c) [...] concentration range of the substances of concern, at the level of the product [...]."@en ;
   samm:characteristic :ConcentrationValue ;
   samm:exampleValue "2.1"^^xsd:float .

:SHORTNAME a samm:Property ;
   samm:preferredName "Short Name"@en ;
   samm:description "The short name of the product. Short names can contain abbreviations."@en ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "8HP60" .

:DigitalProductPassport a samm:Aspect ;
   samm:preferredName "Digital Product Passport"@en ;
   samm:description "The Digital Product Passport (DPP) allows to share process and product-related information amongst supply chain businesses, authorities and consumers. The DPP allows for efficient information flows following best practices; and the possibility of accompanying the measures under this Regulation with mitigating measures so that impacts are expected to remain proportionate for SMEs.This is expected to increase transparency, both for supply chain businesses and for the general public, and increase efficiencies in terms of information transfer to support the data exchange between economic actors in integrating circularity in product design and manufacturing.\nIn particular, it is likely to help facilitate and streamline the monitoring and enforcement of the regulation carried out by EU and Member State authorities. It is also likely to provide a market-intelligence tool that may be used for revising and refining obligations in the future.\nThe DPP includes data about components, materials and chemical substances, information on repairability, spare parts, environmental impact and professional disposal for a product.\nThe data model will be updated, as newer versions of the regulation will be published.\nThis data model is based on the proposed Ecodesign Regulation (\"Proposal for a REGULATION OF THE EUROPEAN PARLIAMENT AND OF THE COUNCIL establishing a framework for setting ecodesign requirements for sustainable products and repealing Directive 2009/125/EC\" ESPR from March 30th, 2022 with some additional data points which can be found in the adopted Amendments from July 12th 2023 (Amendments adopted by the European Parliament on 12 July 2023 on the proposal for a regulation of the European Parliament and of the Council establishing a framework for setting eco-design requirements for sustainable products and repealing Directive 2009/125/EC)."@en ;
   samm:see <https://data.consilium.europa.eu/doc/document/ST-9014-2023-INIT/en/pdf> ;
   samm:see <https://www.europarl.europa.eu/doceo/document/TA-9-2023-0272_EN.html> ;
   samm:see <https://commission.europa.eu/energy-climate-change-environment/standards-tools-and-labels/products-labelling-rules-and-requirements/sustainable-products/ecodesign-sustainable-products_en> ;
   samm:see <https://eur-lex.europa.eu/legal-content/EN/TXT/?uri=CELEX:52022PC0142> ;
   samm:properties (
      :metadata
      :typology
      :SUSTAINABILITY
      :characteristics
      :handling
      :operation
      :sources
   ) ;
   samm:operations ( ) ;
   samm:events ( ) .

:substanceName a samm:Property ;
   samm:preferredName "Substance Name"@en ;
   samm:description "The substance name, in accordance with the specification in the attribute for the list type, which is present in the product. This attribute is mentioned in the ESPR proposal from March 30th, 2022 and changed by the amendments of the European Parliament from July 12th, 2023 Article 7:\n(5) (a) the International Union of Pure and Applied Chemistry (IUPAC) name of the substances of concern present in the product, including the chemical identification number, that is the European Inventory of Existing Commercial Chemical Substances (EINECS) or European List of Notified Chemical Substances (ELINCS) number or Chemical Abstract Service (CAS) number."@en ;
   samm:characteristic :NameCharacteristic .

:weightOrVolume a samm:Property ;
   samm:preferredName "Weight or Volume"@en ;
