   samm:preferredName "Latitude Constraint"@en ;
   samm:description "Regular expression for latitude information of geographic coordinates."@en ;
   samm:value "^(\\+|-)?(?:90(?:(?:\\.0{1,6})?)|(?:[0-9]|[1-8][0-9])(?:(?:\\.[0-9]{1,6})?))$" .

