   samm:properties ( [ samm:property :id; samm:payloadName "id" ] [ samm:property :specVersion; samm:payloadName "specVersion" ] [ samm:property :partialFullPcf; samm:payloadName "partialFullPcf" ] [ samm:property :precedingPfIds; samm:optional true; samm:payloadName "precedingPfIds" ] [ samm:property :version; samm:payloadName "version" ] [ samm:property :created; samm:payloadName "created" ] [ samm:property :status; samm:payloadName "extWBCSD_pfStatus" ] [ samm:property :validityPeriodStart; samm:optional true; samm:payloadName "validityPeriodStart" ] [ samm:property :validityPeriodEnd; samm:optional true; samm:payloadName "validityPeriodEnd" ] [ samm:property :comment; samm:optional true; samm:payloadName "comment" ] [ samm:property :companyName; samm:payloadName "companyName" ] [ samm:property :companyIds; samm:payloadName "companyIds" ] [ samm:property :productDescription; samm:optional true; samm:payloadName "productDescription" ] [ samm:property :productIds; samm:payloadName "productIds" ] [ samm:property :productCategoryCpc; samm:payloadName "extWBCSD_productCodeCpc" ] [ samm:property :productNameCompany; samm:payloadName "productName" ] [ samm:property :pcf; samm:payloadName "pcf" ] [ samm:property :pcfLegalStatement; samm:optional true; samm:payloadName "pcfLegalStatement" ] ) ;
   samm:operations ( ) ;
   samm:events ( ) .

:id a samm:Property ;
   samm:preferredName "Product Footprint Identifier"@en ;
   samm:description "Mandatory: The product footprint identifier as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
