   samm:exampleValue "3893bb5d-da16-4dc1-9185-11d97476c254" .

:specVersion a samm:Property ;
   samm:preferredName "Product Footprint Specification Version"@en ;
   samm:description "Mandatory: Version of the product footprint data specification as defined in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:characteristic samm-c:Text ;
   samm:exampleValue "2.0.1-20230314" .

:partialFullPcf a samm:Property ;
   samm:preferredName "Partial Or Full PCF (Product Carbon Footprint)"@en ;
   samm:description "Mandatory: Indicator for partial or full PCF (Product Carbon Footprint) declaration as specified in the Catena-X PCF Rulebook."@en ;
   samm:characteristic :PartialFullPcfCharacteristic ;
