   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:characteristic :PrecedingPfIdsCharacteristic .

:version a samm:Property ;
   samm:preferredName "Product (Carbon) Footprint Version"@en ;
   samm:description "Mandatory: Version of the product (carbon) footprint as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative. In Catena-X for example set to \"0\" per default."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:characteristic :ProductFootprintVersion ;
   samm:exampleValue "0"^^xsd:nonNegativeInteger .

:created a samm:Property ;
   samm:preferredName "Creation of the Product (Carbon) Footprint"@en ;
   samm:description "Mandatory: Timestamp of the creation of the Product (Carbon) Footprint as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:characteristic samm-c:Timestamp ;
   samm:exampleValue "2022-05-22T21:47:32Z"^^xsd:dateTime .

:status a samm:Property ;
   samm:preferredName "Status"@en ;
