   samm:characteristic :NonEmptyStringTrait ;
   samm:exampleValue "My Corp" .

:companyIds a samm:Property ;
   samm:preferredName "Company IDs"@en ;
   samm:description "Mandatory: Non-empty set of Uniform Resource Names (URN) representing company identifiers. Each value is supposed to uniquely identify the product (carbon) footprint data owner as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:characteristic :IdsTrait ;
   samm:exampleValue "urn:uuid:51131FB5-42A2-4267-A402-0ECFEFAD1619"^^xsd:anyURI .

:productDescription a samm:Property ;
   samm:preferredName "Product Description"@en ;
   samm:description "Optional: Free-form description of the product as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
