   samm-c:constraint :NonEmptyString .

:IdsTrait a samm-c:Trait ;
   samm:preferredName "IDs Trait"@en ;
   samm:description "Trait for URIs (Uniform Resource Identifiers)."@en ;
   samm-c:baseCharacteristic :IdSet ;
   samm-c:constraint :NonEmptySetConstraint .

:CarbonFootprint a samm-c:SingleEntity ;
   samm:preferredName "PCF (Product Carbon Footprint) Characteristic"@en ;
   samm:description "Characteristic for defining a PCF (Product Carbon Footprint) as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:dataType :PcfEntity .

:PrecedingPfId a samm:Entity ;
   samm:preferredName "Preceding PCF (Product Carbon Footprint) Identifier Entity"@en ;
   samm:description "Entity for defining a preceding PCF (Product Carbon Footprint) identifier entity as specified in the Catena-X PCF Rulebook in accordance with the technical specifications for PCF Data Exchange (Version 2.0.0) from the WBCSD (World Business Council for Sustainable Development)/ PACT initiative."@en ;
   samm:see <https://wbcsd.github.io/tr/2023/data-exchange-protocol-20230221/> ;
   samm:properties ( :id ) .

:NonEmptyString a samm-c:LengthConstraint ;
   samm:preferredName "Non Empty String Constraint"@en ;
   samm:description "Constraint for ensuring that a string has at least one character."@en ;
   samm-c:minValue "1"^^xsd:nonNegativeInteger .

:IdSet a samm-c:List ;
   samm:preferredName "ID Set Characteristic"@en ;
   samm:description "Characteristic for defining a set of URIs (Uniform Resource Identifiers)."@en ;
   samm:dataType xsd:anyURI .

:NonEmptySetConstraint a samm-c:LengthConstraint ;
   samm:preferredName "Non Empty Set Constraint"@en ;
   samm:description "Constraint for defining a non-empty set of URIs (Uniform Resource Identifieres)."@en ;
   samm-c:minValue "1"^^xsd:nonNegativeInteger .

